
public class QUEUES {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		final int a = 10;
		for(int i=0;i<a;i+=2)
		{
			for(int j=i;j<a;j++)
			{
				System.out.print("*");
			}
			System.out.println();
		}

	}
	
	/**
	 * Notes:
	 * 
	 * QUEUES are:
	 * 
	 * ==== A Line =====
	 * 
	 * First In, First Out
	 * 
	 * Adding always places new element at the end
	 * 
	 * Removing always takes away first element
	 * 
	 * Enqueue - inserting a new element
	 * Dequeue - removing a new element
	 * 
	 * 
	 */

}
